      }, {
        id: 'A10',
        disponible: true,
        purchased: false
      }]
    };
  },
  methods: {
    seleccionarAsiento: function seleccionarAsiento(event) {
      var asiento = this.asientos.find(function (a) {
        return a.id == event.target.id;
      });

      if (asiento.adquirido || asiento.user_id != null && asiento.user_id != this.id) {
        this.$notify({
          group: 'foo',
          type: 'error',
          title: 'Error',
          text: 'No es posible seleccionar el asiento ' + asiento.id
        });
        return;
      }

      asiento.disponible = !asiento.disponible;
      asiento.user_id = this.id;
      this.actualizarElementos();
      this.contador = this.asientosSeleccionados().length;
    },
    actualizarElementos: function actualizarElementos() {
      firebase.database().ref(path).child(pathId).set(this.asientos);
    },
    validarRespuesta: function validarRespuesta(error, committed, snapshot) {
      if (error) {
        this.$notify({
          group: 'foo',
          type: 'error',
          title: 'Error',
          text: 'No es posible completar la operación'
        });
      }

      if (committed) {
        this.$notify({
          group: 'foo',
          type: 'success',
          title: 'Exito',
          text: 'Asientos adquiridos exitosamente'
        });
      }

      console.log(snapshot.val());
    },
    cargarElementos: function cargarElementos(data) {
      this.asientos = data;
    },
    guardar: function guardar() {
      var _this = this;

      firebase.database().ref(path).child(pathId).transaction(function (valoresDB) {
        return _this.validarCompra(valoresDB);
      }, function (error, committed, snapshot) {
        return _this.validarRespuesta(error, committed, snapshot);
      });
      this.contador = 0;
    },
    validarCompra: function validarCompra(valoresDB) {
      this.asientosSeleccionados().forEach(function (asiento) {
        if (valoresDB.find(function (a) {
          return a.id == asiento.id;
        }).adquirido) {
          return;
        }

        asiento.adquirido = true;
      });
      return this.asientos;
    },
    cancelar: function cancelar() {
      this.asientosSeleccionados().forEach(function (asiento) {
        asiento.user_id = null;
        asiento.disponible = true;
      });
      this.actualizarElementos();
      this.contador = 0;
    },
    validarAsientos: function validarAsientos() {
      this.asientosSeleccionados().forEach(function (asiento) {
        asiento.adquirido = true;
      });
    },
    asientoDiponible: function asientoDiponible(asiento) {
      return !asiento.adquirido && (asiento.user_id == null || asiento.user_id == this.id);
    },
    asientosSeleccionados: function asientosSeleccionados() {
      return this.asientos.filter(function (a) {
        return !a.disponible && !a.adquirido;
      });
    },
    liberar: function liberar() {
      this.asientos.forEach(function (asiento) {
        asiento.adquirido = false;
        asiento.disponible = true;
        asiento.user_id = null;
      });
      this.actualizarElementos();
      this.contador = 0;
    }
  }
};
